import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import Week from './Week';
import EventForm from './EventForm';
import { getTimeline, createEvent } from '../services/api'; // Import getTimeline and createEvent

export default function Timeline() {
  const { currentUser } = useAuth();
  const [timeline, setTimeline] = useState([]);
  const [selectedWeek, setSelectedWeek] = useState(null);
  const [showEventForm, setShowEventForm] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null); // Define setError

  useEffect(() => {
    if (currentUser) {
      loadTimeline();
    }
  }, [currentUser]);

  const loadTimeline = async () => {
    try {
      console.log("Loading timeline...");
      const response = await getTimeline(); // Now defined
      console.log("Timeline response:", response);
      
      const data = response.data;
      console.log("Timeline data:", data);
      
      // Convert date strings to Date objects
      const timelineWithDates = data.weeks.map(week => ({
        ...week,
        start_date: new Date(week.start_date),
        end_date: new Date(week.end_date),
        events: week.events.map(event => ({
          ...event,
          date: new Date(event.date)
        }))
      }));
      
      setTimeline(timelineWithDates);
      setLoading(false);
    } catch (error) {
      console.error("Failed to load timeline", error);
      console.error("Error details:", error.response?.data);
      setError("Failed to load timeline. See console for details."); // Now defined
      setLoading(false);
    }
  };

  const handleWeekClick = (week) => {
    setSelectedWeek(week);
    setShowEventForm(true);
  };

  const handleAddEvent = async (eventData) => {
    try {
      await createEvent({
        ...eventData,
        date: selectedWeek.start_date.toISOString().split('T')[0]
      });
      loadTimeline();
      setShowEventForm(false);
    } catch (error) {
      console.error("Failed to add event", error);
    }
  };

  if (error) {
    return <div className="error">{error}</div>;
  }

  if (loading) {
    return <div>Loading timeline...</div>;
  }

  // Group weeks by year
  const years = {};
  timeline.forEach(week => {
    const year = week.start_date.getFullYear();
    if (!years[year]) years[year] = [];
    years[year].push(week);
  });

  return (
    <div className="timeline-container">
      <div className="years-grid">
        {Object.entries(years).map(([year, weeks]) => (
          <div key={year} className="year-row">
            <div className="year-label">{year}</div>
            <div className="weeks-container">
              {weeks.map(week => (
                <Week 
                  key={week.week_number} 
                  week={week} 
                  onClick={() => handleWeekClick(week)}
                />
              ))}
            </div>
          </div>
        ))}
      </div>

      {showEventForm && selectedWeek && (
        <EventForm 
          week={selectedWeek}
          onSubmit={handleAddEvent}
          onCancel={() => setShowEventForm(false)}
        />
      )}
    </div>
  );
}
