// src/services/api.js
import axios from 'axios';

const api = axios.create({
  baseURL: process.env.REACT_APP_API_BASE_URL
});

// Add token to requests
api.interceptors.request.use(config => {
  const user = JSON.parse(localStorage.getItem('user'));
  if (user && user.token) {
    config.headers.Authorization = `Bearer ${user.token}`;
  } else {
    console.error("No user token found");
  }
  return config;
});

// Add response interceptor for token refresh
api.interceptors.response.use(
  response => response,
  error => {
    if (error.response?.status === 401) {
      // Token expired, logout user
      localStorage.removeItem('user');
      window.location.reload();
    }
    return Promise.reject(error);
  }
);

// Export functions
export const getTimeline = () => api.get('/timeline/timeline');
export const createEvent = (event) => api.post('/events/events/', event);
export const getEvents = () => api.get('/events/events/');
export const updateEvent = (id, event) => api.put(`/events/events/${id}`, event);
export const deleteEvent = (id) => api.delete(`/events/events/${id}`);
